import tokenizer from './tokenizer';
export interface IVariable {
    name: string;
    value: string;
    offset: number;
}
export interface IImport {
    filepath: string;
    dynamic: boolean;
    css: boolean;
}
export interface IMixin {
    name: string;
    parameters: IVariable[];
    offset: number;
}
export interface IFunction extends IMixin {
}
declare function parseSymbols(text: string): {
    variables: IVariable[];
    mixins: IMixin[];
    functions: IFunction[];
    imports: IImport[];
};
export { parseSymbols, tokenizer };
